import React, { useEffect, useState } from 'react';
import createBoard from '../util/createBoard';
import Cell from './Cell';
import { revealed } from '../util/reveal';

const Board = () => {
    const [grid, setGrid] = useState([]);
    const [nonMineCount, setNonMineCount] = useState(0);
    const [mineLocations, setMineLocation] = useState([]);

    useEffect(() => {
        // Creating a board
        function freshBoard(){
            const newBoard = createBoard(10, 10, 15);
            setNonMineCount(10 * 10 - 15);
            setMineLocation(newBoard.mineLocation);
            setGrid(newBoard.board);
        }
        // Calling the function
        freshBoard();
    }, []);

    // On Right Click / Flag Cell
    const updateFlag = (e, x, y) => {
        // To not have a dropdown on right click
        e.preventDefault();
        // Deep copy of a state
        let newGrid = JSON.parse(JSON.stringify(grid));
        console.log(newGrid[x][y]);
        newGrid[x][y].value = 'ðŸš©';
        newGrid[x][y].revealed = true;
        newGrid[x][y].flagged = true;
        setGrid(newGrid);
    };

    // Reveal Cell
    const revealCell = (x, y) => {
        if(grid[x][y].revealed){

        }
        let newGrid = JSON.parse(JSON.stringify(grid));
        if(newGrid[x][y].value === 'ðŸ’£'){
            alert("mine find");
            for(let i = 0; i < mineLocations.length; i++){
                newGrid[mineLocations[i][0]][mineLocations[i][1]].revealed = true;
            }
            setGrid(newGrid);
        } 
        else{
            let newRevealedBoard = revealed(newGrid, x, y, nonMineCount);
            setGrid(newRevealedBoard.arr);
            setNonMineCount(newRevealedBoard.newNonMinesCount)
        }


    }

    // if (!grid.board){
    //     return <div>Loading...</div>;
    // }

    return(
        <div>
            <p>{nonMineCount}</p>
            <div style = {{display: "flex", flexDirection: "column", alignItems: "center"}}>
                {
                    grid.map((singleRow, index1) => {
                        return (
                            <div style = {{display: "flex"}} key = {index1}>
                                {singleRow.map((singleBlock, index2) => {
                                    return (
                                        <Cell detail = {singleBlock} updateFlag = {updateFlag} revealCell = {revealCell} key = {index2}/>
                                    );
                                })}
                            </div>
                        );
                    })
                }
            </div>

        </div>
    ); 



}

export default Board
