import "../App.css"

export default function Dashboard({remainFlagNum, gameOver}){
    let [time, setTime] = useState(0);
    let [over, setOver] = useState(false);



    useEffect(() => {
        console.log("useeffect", over)
        function incrementTime(){
            setTimeout(() => {
                let newTime = time + 1;
                setTime(newTime);
            }, 1000);
        }
        function restartTimer(){
            setTimeout(() => {
                let newTime = 0;
                setTime(newTime);
            }, );

        }
        function stopTime(){
            setTimeout(() => {
                let newTime = time;
                setTime(newTime);
            }, 1000);
        }
        if(over && !gameOver){
            console.log('restart')
            restartTimer();
            setOver(false);
        } 
        if(!gameOver) incrementTime();
        else {
            stopTime();
            setOver(true);
        }


    }, [time]);


    return (
        <div className = "dashBoard">

            <div id = 'dashBoard_col1' >
                <div className = 'dashBoard_col'>
                    <p className = 'icon'>üö© </p>
                    {remainFlagNum}
                </div>
            </div>
            <div id = 'dashBoard_col2' >
                <div className = 'dashBoard_col'>
                    <p className = 'icon'>‚è∞</p>
                    {/* {gameOver ? restartTimer() : time} */}
                </div>
            </div>
        </div>
    );
}
