import React, { useEffect, useState } from "react";
import "../App.css"

export default function Modal({restartGame}){
    const [render, setRender] = useState(false);
    useEffect(() => {
        setTimeout(() => {
            setRender(true);
        }, 1000);
    }, []);

    return (
        <div className="Modal" 
            style = {{
                opacity: render ? 1 : 0,
                height: "100%",
                width: "100%",
                position: "absolute",
                background: "rgba(0, 0, 0, 0.3)",
                border: "1px solid red"

            }}>
            <div className = "Modal_content">
                <div id = "gameOverImage">GameOver</div>
                <div className = "tryAgain" onClick = {() => restartGame()}>Try Again</div>
            </div>
        </div>

    );
}
