import React from "react";

export default function Cell({detail, updateFlag, revealCell}){


    return (
        <div>
            <div 
                onContextMenu = {(e) => updateFlag(e, detail.x, detail.y) }
                onClick = {() => revealCell(detail.x, detail.y)}
                style = {style.cellStyle}
            >
                {detail.revealed ? detail.value != 0 && detail.value : ""}
                {/* {detail.value != 0 && detail.value} */}
            </div>
        </div>
    );
}

const style = {
    cellStyle: {
        width : 40,
        height : 40,
        border: "1px solid white",
        justifyContent: "center",
        background: "lightgrey",
        alignItems: "center",
        display: "flex",
        cursor: 'pointer',
        fontSize: 25,
    },
};

const bombCheckPattern = (x, y) => {
    if(x % 2 === 0 && y % 2 === 0) return '#e5c29f';
    else if (x % 2 === 0 && y % 2 !== 0) return '#d7b899';
    else if (x % 2 !== 0 && y % 2 === 0)  return '#d7b899';
    else return '#e5c29f';


}
