        function incrementTime(){
            setTimeout(() => {
                let newTime = time + 1;
                setTime(newTime);
            }, 1000);
        }
        incrementTime();
    }, [time]);

    console.log(timeIntervalId);
    return (
        <div className = 'dashBoard_col'>
            <img className= 'clock' src='https://www.google.com/logos/fnbx/minesweeper/clock_icon.png' style = {{ paddingRight: 10 }}></img>
            {time}
        </div>
    );
}
