import Timer from './Timer';
import "../App.css"

export default function Dashboard({remainFlagNum}){
    let [time, setTime] = useState(0);

    useEffect(() => {
        function incrementTime(){
            setTimeout(() => {
                let newTime = time + 1;
                setTime(newTime);
            }, 1000);
        }
        incrementTime();
    }, [time]);

    return (
        <div className = "dashBoard">
            <div id = 'dashBoard_col1' >
                <div className = 'dashBoard_col'>
                    <p className = 'icon'>ðŸš© </p>
                    {/* <img className= 'clock' src = 'https://www.google.com/logos/fnbx/minesweeper/flag_icon.png' style = {{ paddingRight: 10 }}></img> */}
                    {remainFlagNum}
                </div>
            </div>
            <div id = 'dashBoard_col2' >
                <div className = 'dashBoard_col'>
                    <p className = 'icon'>ðŸš© </p>
                        {/* <img className= 'clock' src = 'https://www.google.com/logos/fnbx/minesweeper/flag_icon.png' style = {{ paddingRight: 10 }}></img> */}
                    {time}
                </div>
            </div>
        </div>
    );
}
