import React, { useEffect, useState } from "react";
import "../App.css"

export default function Modal({reset}){
    const [render, setRender] = useState(false);
    useEffect(() => {
        setTimeout(() => {
            setRender(true);
        }, 1000);
    }, []);

    return (
        <div className="Modal" style = {{opacity: render ? 1 : 0}}>
            <div id = "gameOverImage"></div>
            <div className = "tryAgain">Try Again</div>

        </div>

    );
}
