export default (row, col, bombs) => {
    let board = [];
    let mineLocation = [];

    // Create a blank board
    for(let x = 0; x < row; x++){
        let subCol = [];
        for(let y = 0; y < col; y++){
            subCol.push({
                value: 0,
                revealed: false,
                neighbors: [],
                x: x,
                y: y,
                flagged: false,
            });
        }
        board.push(subCol);
    }

    let bombsCount = 0;
    while(bombsCount < bombs){
        let x = randomNum(0, row - 1);
        let y = randomNum(0, col - 1);

        if(board[x][y].value === 0){
            board[x][y].value = "X";
            mineLocation.push([x, y]);
            bombsCount++;
        }
    }



}
