import React, { useEffect, useState } from 'react';
import createBoard from '../util/createBoard';
import Cell from './Cell';
import Modal from './Modal';
import { revealed } from '../util/reveal';
import "../App.css"
import Dashboard from './Dashboard';
let row = 5;
let col = 5;
let bomb = 2;

const Board = () => {
    const [grid, setGrid] = useState([]);
    const [nonMineCount, setNonMineCount] = useState(0);
    const [mineLocations, setMineLocation] = useState([]);
    const [gameOver, setGameOver] = useState(false);
    const [remainFlagNum, setRemainFlagNum] = useState(0);
    const [win, setWin] = useState(false);

    useEffect(() => {
        // Calling the function
        freshBoard();
    }, []);

    // Creating a board
    const freshBoard = () => {
        const newBoard = createBoard(row, col, bomb);
        setNonMineCount(10 * 10 - 15);
        setRemainFlagNum(15);
        setMineLocation(newBoard.mineLocation);
        setGrid(newBoard.board);
    }

    const restartGame = () => {
        freshBoard();
        setGameOver(false);
    }

    // On Right Click / Flag Cell
    const updateFlag = (e, x, y) => {
        // To not have a dropdown on right click
        e.preventDefault();
        // Deep copy of a state
        let newGrid = JSON.parse(JSON.stringify(grid));
        let newFlagNum = remainFlagNum;
        if(newGrid[x][y].flagged !== true &&  newGrid[x][y].revealed !== true){
            newGrid[x][y].value = 'ðŸš©';
            newGrid[x][y].revealed = true;
            newGrid[x][y].flagged = true;
            newFlagNum--;
        }
        else{
            newGrid[x][y].value = '';
            newGrid[x][y].revealed = false;
            newGrid[x][y].flagged = false;
            newFlagNum++;
        }
        setRemainFlagNum(newFlagNum);
        setGrid(newGrid);
        checkWin();
    };

    // Reveal Cell
    const revealCell = (x, y) => {
        if(grid[x][y].revealed || gameOver){
            return;
        }
        let newGrid = JSON.parse(JSON.stringify(grid));
        if(newGrid[x][y].value === 'ðŸ’£'){
            for(let i = 0; i < mineLocations.length; i++){
                newGrid[mineLocations[i][0]][mineLocations[i][1]].revealed = true;
            }
            setGrid(newGrid);
            setGameOver(true);
        } 
        else{
            let newRevealedBoard = revealed(newGrid, x, y, nonMineCount);
            setGrid(newRevealedBoard.arr);
            setNonMineCount(newRevealedBoard.newNonMinesCount);
            if(newRevealedBoard.newNonMinesCount === 0){
                setGameOver(true);
            }
        }
        checkWin();
    }

    const checkWin = () => {
        for(let x = 0; x < row; x++){
            for(let y = 0 ; y < col; y++){
                console.log(grid[x][y].value);
            }
        }
        return true;
    }

    return(
        <div className='boardWrapper' style = {{width : 400}}>
            <Dashboard remainFlagNum = {remainFlagNum} gameOver = {gameOver}/>
            <div className="board">
                {gameOver && <Modal restartGame = {restartGame}/>}
                {
                    grid.map((singleRow, index1) => {
                        return (
                            <div style = {{display: "flex"}} key = {index1}>
                                {singleRow.map((singleBlock, index2) => {
                                    return (
                                        <Cell detail = {singleBlock} updateFlag = {updateFlag} revealCell = {revealCell} key = {index2}/>
                                    );
                                })}
                            </div>
                        );
                    })
                }
            </div>

        </div>
    ); 



}

export default Board
